import axios from "axios"
import { USER_INFO_FAIL, USER_INFO_REQUEST, USER_INFO_SUCCESS } from "../constants/userInfo"

const infoUser = (userName) => async (dispatch) => {
    
    try {
        dispatch({type: USER_INFO_REQUEST})
        const {data} = await axios.get(`https://api.github.com/users/${userName}`)
        data.repos = await axios.get(`https://api.github.com/users/${userName}/repos?per_page=2`)
        console.log(data)
        dispatch({type: USER_INFO_SUCCESS, payload: data})
    } catch (error) {
        dispatch({type: USER_INFO_FAIL, payload: error})
    }
} 

export {infoUser}
