import { USER_INFO_FAIL, USER_INFO_REQUEST, USER_INFO_SUCCESS } from "../constants/userInfo";

function userInfoReducer(state= {userInfo: {}, url: '', avatar_url: '', login: '', name: '', followers: 0, following: 0}, action) {
    switch (action.type) {
        case USER_INFO_REQUEST:
            return {loading: true}
        case USER_INFO_SUCCESS: 
        console.log(action.payload)
            return {loading: false, url: action.payload.url}
        case USER_INFO_FAIL:
            return {loading: false}
        default:
            return state
    }
}

export {userInfoReducer}
