import { USER_INFO_FAIL, USER_INFO_REQUEST, USER_INFO_SUCCESS } from "../constants/userInfo";

function userInfoReducer(state= {userInfo: {}}, action) {
    switch (action.type) {
        case USER_INFO_REQUEST:
            return {loading: true}
        case USER_INFO_SUCCESS: 
            return {loading: false, data: action.payload}
        case USER_INFO_FAIL:
            return {loading: false}
        default:
            return state
    }
}

export {userInfoReducer}
