import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
import Header from './components/Header/Header'
import Skeleton from '@material-ui/lab/Skeleton';

function Main() {

    const userInfo = useSelector(state => state.userInfo)
    const { user, html_url, name, login, loading, error, avatar_url } = userInfo
    const dispatch = useDispatch()
    return (
        <div>
            <Header/>
            <div style={styles.content} className="content">
            {error ? "error" : ''}
            {!user ? "ssss" : ''}
            <img style={{borderRadius: "50%"}} src={avatar_url}></img>
            {loading? <Skeleton animation="wave"><h2>Чтобы был красивый скелетон)0)</h2></Skeleton> : 
            error ? <h2 style={{color: "black"}}>Произошла ошибка {error.message}</h2> : 
            <h2 style={{color: "black"}}>{name ? name : login}</h2>}
            <a href={html_url} style={{textDecoration: "none", color: "#0064EB"}} target="_blank">{login}</a>
            </div>
            
        </div>
    )
}

const styles = {
    content : {
        background: "#F9F9F9",
        height: "calc(100vh - 72px)"
    }
}

export default Main
